import React, { useRef, useState } from "react";
import axios from "axios";
import "./Record.css";

const Record = () => {
  const [isRecording, setIsRecording] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const contextRef = useRef(null);

  const startRecording = async () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    contextRef.current = context;

    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    video.srcObject = stream;
    video.play();

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    if (isRecording) {
      const interval = setInterval(() => {
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        const frameData = canvas.toDataURL("image/jpeg");

        axios.post('http://localhost:5000/process-frame', { image: frameData })
          .then(response => {
            const img = new Image();
            img.src = `data:image/jpeg;base64,${response.data.processed_image}`;
            context.drawImage(img, 0, 0);
          })
          .catch(error => console.error("Error processing frame:", error));
      }, 100);

      return () => clearInterval(interval);
    }
  };

  return (
    <div className="record-container">
      <video ref={videoRef} className="video" style={{ display: "none" }} />
      <canvas ref={canvasRef} className="canvas" />
      <div className="controls">
        <button className="button" onClick={() => setIsRecording(!isRecording)}>
          {isRecording ? "Stop" : "Start"} Recording
        </button>
      </div>
    </div>
  );
};

export default Record;
